const PostList = () => {
    const PostsState = useSelector(state => state.posts)

    const dispatch  = useDispatch()


    useEffect(() => {
        dispatch(fetchPosts())
    },[dispatch])

    return (
    <div>{PostsState.title}</div>
    )

}

